import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';

export default function Home() {
  const navigate = useNavigate();
  const { state, createGame, joinGame } = useGame();
  const [playerName, setPlayerName] = useState('');
  const [gameId, setGameId] = useState('');
  const [showJoinForm, setShowJoinForm] = useState(false);
  const [selectedMode, setSelectedMode] = useState<'casual' | 'competitive'>('casual');

  // Check for existing game session
  useEffect(() => {
    const existingGameId = localStorage.getItem('currentGameId');
    const existingPlayerId = localStorage.getItem('currentPlayerId');
    
    if (existingGameId && existingPlayerId) {
      setGameId(existingGameId);
    }
  }, []);

  // Navigate to game when one is created/joined
  useEffect(() => {
    if (state.currentGame) {
      if (state.currentGame.status === 'draft') {
        navigate(`/lobby/${state.currentGame.id}`);
      } else if (state.currentGame.status === 'live') {
        navigate(`/game/${state.currentGame.id}`);
      } else if (state.currentGame.status === 'ended') {
        navigate(`/recap/${state.currentGame.id}`);
      }
    }
  }, [state.currentGame, navigate]);

  const handleCreateGame = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!playerName.trim()) return;
    
    await createGame(selectedMode, playerName.trim());
  };

  const handleJoinGame = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!playerName.trim() || !gameId.trim()) return;
    
    await joinGame(gameId.trim().toUpperCase(), playerName.trim());
  };

  const handleQuickJoin = () => {
    const existingGameId = localStorage.getItem('currentGameId');
    if (existingGameId && state.currentGame) {
      if (state.currentGame.status === 'draft') {
        navigate(`/lobby/${existingGameId}`);
      } else if (state.currentGame.status === 'live') {
        navigate(`/game/${existingGameId}`);
      } else if (state.currentGame.status === 'ended') {
        navigate(`/recap/${existingGameId}`);
      }
    }
  };

  const addDemoPlayer = async () => {
    if (!state.currentGame || !gameId) return;
    
    const demoNames = ['Alice', 'Bob', 'Charlie', 'Diana', 'Eve', 'Frank'];
    const usedNames = state.currentGame.players.map(p => p.name);
    const availableNames = demoNames.filter(name => !usedNames.includes(name));
    
    if (availableNames.length === 0) {
      alert('No more demo players available');
      return;
    }
    
    const demoName = availableNames[0];
    await joinGame(gameId, demoName);
  };

  const switchPlayer = (playerId: string) => {
    if (!state.currentGame) return;
    
    const player = state.currentGame.players.find(p => p.id === playerId);
    if (player) {
      localStorage.setItem('currentPlayerId', playerId);
      window.location.reload();
    }
  };

  const quickTestGame = async () => {
    // Create a game with multiple players and set it to live for testing
    await createGame('casual', 'TestHost');
    
    // Wait for game to be created, then add demo players
    setTimeout(async () => {
      const demoNames = ['Alice', 'Bob', 'Charlie'];
      for (const name of demoNames) {
        await joinGame(state.currentGame?.id || '', name);
      }
    }, 100);
  };

  const clearDemoData = () => {
    localStorage.removeItem('gameData');
    localStorage.removeItem('currentGameId');
    localStorage.removeItem('currentPlayerId');
    localStorage.removeItem('playerToken');
    window.location.reload();
  };

  return (
    <div className="flex flex-column flex-center" style={{ minHeight: '100vh' }}>
      <div style={{ maxWidth: '400px', width: '100%', padding: '1rem' }}>
        <div className="text-center" style={{ marginBottom: '1.5rem' }}>
          <h1 style={{ marginBottom: '0.5rem' }}>Who Got Who</h1>
          <p className="text-small" style={{ fontSize: '0.9rem', opacity: 0.9, color: 'rgba(255, 255, 255, 0.9)' }}>
            The ultimate party game of stealth tasks and gotchas!
          </p>
        </div>

        {state.error && (
          <div className="card card-warning" style={{ marginBottom: '0.75rem' }}>
            <p><strong>‚ùå {state.error}</strong></p>
          </div>
        )}

        {/* Quick Rejoin */}
        {gameId && state.currentGame && (
          <div className="card card-success" style={{ marginBottom: '0.75rem' }}>
            <h3 style={{ fontSize: '1.1rem', marginBottom: '0.5rem' }}>üéÆ Continue Game</h3>
            <p className="text-small" style={{ marginBottom: '0.75rem' }}>
              <strong>Game ID:</strong> {gameId}<br/>
              <strong>Status:</strong> {state.currentGame.status}<br/>
              <strong>Players:</strong> {state.currentGame.players.length}
            </p>
            <button 
              className="btn btn-success" 
              onClick={handleQuickJoin}
              disabled={state.isLoading}
            >
              {state.currentGame.status === 'draft' ? 'üöÄ Return to Lobby' :
               state.currentGame.status === 'live' ? '‚ö° Rejoin Game' :
               'üèÜ View Results'}
            </button>
            
            {state.currentGame.status === 'draft' && state.currentGame.players.length < 8 && (
              <button 
                className="btn btn-secondary btn-small"
                onClick={addDemoPlayer}
                disabled={state.isLoading}
                style={{ marginTop: '0.5rem' }}
              >
                ‚ûï Add Demo Player ({state.currentGame.players.length}/8)
              </button>
            )}
          </div>
        )}

        {/* Main game creation/join */}
        {!showJoinForm ? (
          <div>
            {/* Create Game Form */}
            <div className="card">
              <h2 style={{ fontSize: '1.3rem', marginBottom: '1rem' }}>üéÆ Create New Game</h2>
              <form onSubmit={handleCreateGame}>
                <input
                  type="text"
                  className="input"
                  placeholder="Your name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  maxLength={20}
                  required
                />
                
                <div style={{ margin: '1rem 0' }}>
                  <h3 style={{ fontSize: '1rem', marginBottom: '0.75rem' }}>üéØ Game Mode</h3>
                  <div style={{ margin: '0.5rem 0' }}>
                    <label className="flex" style={{ alignItems: 'flex-start', marginBottom: '0.75rem', cursor: 'pointer' }}>
                      <input
                        type="radio"
                        value="casual"
                        checked={selectedMode === 'casual'}
                        onChange={(e) => setSelectedMode(e.target.value as 'casual')}
                      />
                      <div>
                        <strong style={{ color: '#56ab2f' }}>üåü Casual</strong>
                        <div className="text-small">2 swaps ‚Ä¢ No penalties ‚Ä¢ Fun focus</div>
                      </div>
                    </label>
                    <label className="flex" style={{ alignItems: 'flex-start', cursor: 'pointer' }}>
                      <input
                        type="radio"
                        value="competitive"
                        checked={selectedMode === 'competitive'}
                        onChange={(e) => setSelectedMode(e.target.value as 'competitive')}
                      />
                      <div>
                        <strong style={{ color: '#ff416c' }}>‚öîÔ∏è Competitive</strong>
                        <div className="text-small">1 swap ‚Ä¢ Penalties ‚Ä¢ Strategy focus</div>
                      </div>
                    </label>
                  </div>
                </div>

                <button 
                  type="submit" 
                  className="btn" 
                  disabled={state.isLoading || !playerName.trim()}
                >
                  {state.isLoading ? 'üîÑ Creating...' : 'üöÄ Create Game'}
                </button>
              </form>
            </div>

            <div className="text-center" style={{ margin: '0.75rem 0' }}>
              <button 
                className="btn btn-secondary" 
                onClick={() => setShowJoinForm(true)}
              >
                üîó Join Existing Game
              </button>
            </div>
          </div>
        ) : (
          <div>
            {/* Join Game Form */}
            <div className="card">
              <h2 style={{ fontSize: '1.3rem', marginBottom: '1rem' }}>üîó Join Game</h2>
              <form onSubmit={handleJoinGame}>
                <input
                  type="text"
                  className="input"
                  placeholder="Your name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  maxLength={20}
                  required
                />
                <input
                  type="text"
                  className="input"
                  placeholder="Game ID (e.g. ABC123)"
                  value={gameId}
                  onChange={(e) => setGameId(e.target.value.toUpperCase())}
                  maxLength={10}
                  required
                />
                <button 
                  type="submit" 
                  className="btn" 
                  disabled={state.isLoading || !playerName.trim() || !gameId.trim()}
                >
                  {state.isLoading ? 'üîÑ Joining...' : 'üéØ Join Game'}
                </button>
              </form>
            </div>

            <div className="text-center" style={{ margin: '0.75rem 0' }}>
              <button 
                className="btn btn-secondary" 
                onClick={() => setShowJoinForm(false)}
              >
                ‚Üê Create New Game Instead
              </button>
            </div>
          </div>
        )}

        {/* Demo Mode Notice - Compact */}
        <div className="card card-demo" style={{ marginBottom: '0.75rem' }}>
          <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem' }}>üì± Local Demo Mode</h3>
          <p className="text-small">
            Testing mode ‚Ä¢ Multiple players ‚Ä¢ No backend needed
          </p>
          {gameId && state.currentGame && (
            <button 
              className="btn btn-danger btn-small"
              onClick={clearDemoData}
              style={{ marginTop: '0.5rem' }}
            >
              üóëÔ∏è Clear Data
            </button>
          )}
        </div>

        {/* Quick Test Setup - Only show if no game */}
        {!gameId && (
          <div className="card" style={{ background: 'rgba(138, 43, 226, 0.1)', marginBottom: '0.75rem' }}>
            <button 
              className="btn"
              onClick={quickTestGame}
              disabled={state.isLoading}
              style={{ background: 'linear-gradient(135deg, #8a2be2, #1e90ff)' }}
            >
              üöÄ Quick Demo (4 Players)
            </button>
          </div>
        )}

        {/* Player Switching - Only when multiple players */}
        {state.currentGame && state.currentGame.players.length > 1 && (
          <div className="card card-test" style={{ marginBottom: '0.75rem' }}>
            <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem' }}>üîÑ Switch Player View</h3>
            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.25rem' }}>
              {state.currentGame.players.map(player => (
                <button
                  key={player.id}
                  className={`btn btn-small ${player.id === state.currentPlayer?.id ? 'btn-success' : 'btn-secondary'}`}
                  onClick={() => switchPlayer(player.id)}
                  style={{ margin: '0.25rem' }}
                >
                  {player.name} {player.isHost ? 'üëë' : ''}
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Game Rules - Compact */}
        <div className="card" style={{ marginTop: '1rem' }}>
          <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem' }}>üìã Quick Rules</h3>
          <div className="text-small" style={{ lineHeight: '1.6' }}>
            üéØ Complete stealth tasks on others<br/>
            üó£Ô∏è Say "Gotcha!" when done<br/>
            üèÜ First to 4 wins<br/>
            ‚öñÔ∏è Targets can dispute<br/>
            üîÑ Swap tasks before starting
          </div>
        </div>

        {/* Connection Status */}
        <div className="text-center text-small" style={{ marginTop: '0.75rem', opacity: 0.6, color: 'rgba(255, 255, 255, 0.8)' }}>
          Status: {state.isConnected ? 'üü¢ Connected' : 'üî¥ Local Demo Mode'}
        </div>
      </div>
    </div>
  );
}
