import React from 'react';
import { TaskInstance, Player } from '../types';

interface TargetSelectModalProps {
  task: TaskInstance;
  players: Player[];
  onSelectTarget: (targetId: string) => void;
  onCancel: () => void;
}

export default function TargetSelectModal({ 
  task, 
  players, 
  onSelectTarget, 
  onCancel 
}: TargetSelectModalProps) {
  return (
    <div className="modal-overlay">
      <div className="modal">
        <h3>ðŸŽ¯ Claim Gotcha!</h3>
        
        <div className="card" style={{ margin: '1rem 0', backgroundColor: '#333' }}>
          <p><strong>Task:</strong></p>
          <p>"{task.text}"</p>
        </div>
        
        <p>Who did you complete this task on?</p>
        
        <div style={{ margin: '1rem 0' }}>
          {players.map((player: Player) => (
            <button
              key={player.id}
              className="btn btn-secondary"
              onClick={() => onSelectTarget(player.id)}
              style={{ 
                margin: '0.25rem',
                width: 'auto',
                display: 'inline-block'
              }}
            >
              {player.name}
            </button>
          ))}
        </div>
        
        <div className="text-small" style={{ 
          margin: '1rem 0', 
          padding: '0.5rem', 
          backgroundColor: '#444', 
          borderRadius: '4px' 
        }}>
          <strong>Remember:</strong> You must say "Gotcha!" out loud before claiming.
          If your target calls you out first, the task fails permanently!
        </div>
        
        <button 
          className="btn btn-danger" 
          onClick={onCancel}
          style={{ marginTop: '1rem' }}
        >
          Cancel
        </button>
      </div>
    </div>
  );
}
