import { Task, TaskPack } from '../types';

// Core Pack A - General stealth tasks for party settings
export const corePackATasks: Task[] = [
  // Social tasks (difficulty 1-2)
  { id: 'task-001', text: 'Get someone to compliment your outfit', difficulty: 1, category: 'social', tags: ['conversation', 'appearance'] },
  { id: 'task-002', text: 'Make someone laugh out loud within 2 minutes', difficulty: 2, category: 'social', tags: ['humor', 'conversation'] },
  { id: 'task-003', text: 'Get someone to high-five you', difficulty: 1, category: 'social', tags: ['gesture', 'celebration'] },
  { id: 'task-004', text: 'Have someone repeat a made-up word you use in conversation', difficulty: 2, category: 'social', tags: ['conversation', 'creativity'] },
  { id: 'task-005', text: 'Get someone to agree with you about a random opinion', difficulty: 1, category: 'social', tags: ['conversation', 'agreement'] },
  
  // Observation tasks (difficulty 2-3)
  { id: 'task-006', text: 'Get someone to check their phone while talking to you', difficulty: 2, category: 'observation', tags: ['behavior', 'distraction'] },
  { id: 'task-007', text: 'Make someone touch their face or hair', difficulty: 3, category: 'observation', tags: ['body language', 'subconscious'] },
  { id: 'task-008', text: 'Get someone to look behind them', difficulty: 2, category: 'observation', tags: ['direction', 'attention'] },
  { id: 'task-009', text: 'Make someone say the word "interesting"', difficulty: 3, category: 'observation', tags: ['conversation', 'specific word'] },
  { id: 'task-010', text: 'Get someone to adjust their clothing', difficulty: 2, category: 'observation', tags: ['appearance', 'self-consciousness'] },
  
  // Action tasks (difficulty 2-4)
  { id: 'task-011', text: 'Have someone demonstrate how to do something with their hands', difficulty: 3, category: 'action', tags: ['demonstration', 'gestures'] },
  { id: 'task-012', text: 'Get someone to walk to a different part of the room', difficulty: 2, category: 'action', tags: ['movement', 'location'] },
  { id: 'task-013', text: 'Make someone count to three out loud', difficulty: 2, category: 'action', tags: ['counting', 'vocal'] },
  { id: 'task-014', text: 'Get someone to tell you their favorite color', difficulty: 1, category: 'action', tags: ['personal', 'preference'] },
  { id: 'task-015', text: 'Have someone make a sound effect', difficulty: 3, category: 'action', tags: ['sound', 'expression'] },
  
  // Misdirection tasks (difficulty 3-4)
  { id: 'task-016', text: 'Get someone to spell a word backwards', difficulty: 4, category: 'misdirection', tags: ['spelling', 'mental challenge'] },
  { id: 'task-017', text: 'Make someone guess what you\'re thinking', difficulty: 3, category: 'misdirection', tags: ['guessing', 'mind reading'] },
  { id: 'task-018', text: 'Get someone to describe their morning routine', difficulty: 3, category: 'misdirection', tags: ['personal', 'routine'] },
  { id: 'task-019', text: 'Have someone explain a simple concept in detail', difficulty: 4, category: 'misdirection', tags: ['explanation', 'detail'] },
  { id: 'task-020', text: 'Make someone question if they said something correctly', difficulty: 4, category: 'misdirection', tags: ['doubt', 'self-questioning'] },
  
  // Creative tasks (difficulty 2-5)
  { id: 'task-021', text: 'Get someone to imitate an animal sound', difficulty: 3, category: 'creative', tags: ['imitation', 'animal'] },
  { id: 'task-022', text: 'Have someone create a handshake with you', difficulty: 4, category: 'creative', tags: ['collaboration', 'gesture'] },
  { id: 'task-023', text: 'Make someone draw something in the air with their finger', difficulty: 3, category: 'creative', tags: ['drawing', 'imagination'] },
  { id: 'task-024', text: 'Get someone to sing or hum a few notes', difficulty: 4, category: 'creative', tags: ['music', 'vocal'] },
  { id: 'task-025', text: 'Have someone tell you a quick made-up story', difficulty: 5, category: 'creative', tags: ['storytelling', 'creativity'] },
  
  // Subtle manipulation tasks (difficulty 3-5)
  { id: 'task-026', text: 'Get someone to say "you\'re right" in agreement', difficulty: 3, category: 'manipulation', tags: ['agreement', 'validation'] },
  { id: 'task-027', text: 'Make someone ask you a question about yourself', difficulty: 4, category: 'manipulation', tags: ['curiosity', 'personal'] },
  { id: 'task-028', text: 'Get someone to offer you something (food, drink, etc.)', difficulty: 4, category: 'manipulation', tags: ['generosity', 'offering'] },
  { id: 'task-029', text: 'Have someone repeat back something you just said', difficulty: 3, category: 'manipulation', tags: ['repetition', 'confirmation'] },
  { id: 'task-030', text: 'Make someone apologize for something minor', difficulty: 5, category: 'manipulation', tags: ['apology', 'minor fault'] },
  
  // Bonus tasks for variety (difficulty 1-4)
  { id: 'task-031', text: 'Get someone to mention food or being hungry', difficulty: 2, category: 'suggestion', tags: ['food', 'hunger'] },
  { id: 'task-032', text: 'Have someone check the time', difficulty: 2, category: 'suggestion', tags: ['time', 'awareness'] },
  { id: 'task-033', text: 'Make someone use the word "exactly"', difficulty: 3, category: 'linguistic', tags: ['specific word', 'agreement'] },
  { id: 'task-034', text: 'Get someone to mention the weather', difficulty: 2, category: 'conversation', tags: ['weather', 'small talk'] },
  { id: 'task-035', text: 'Have someone nod while you\'re talking', difficulty: 1, category: 'body language', tags: ['agreement', 'nonverbal'] },
];

export const corePackA: TaskPack = {
  id: 'core-a',
  name: 'Core Pack A',
  description: 'Essential stealth tasks perfect for any party or gathering. Tested and balanced for maximum fun!',
  tasks: corePackATasks,
  difficulty: 'medium',
  minPlayers: 3,
  maxPlayers: 8
};

// Helper function to get random tasks for a player
export function getRandomTasks(packId: string, count: number = 7): Task[] {
  const pack = getTaskPack(packId);
  if (!pack) return [];
  
  const shuffled = [...pack.tasks].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, count);
}

// Helper function to get a task pack by ID
export function getTaskPack(packId: string): TaskPack | null {
  switch (packId) {
    case 'core-a':
      return corePackA;
    // Future packs would go here
    default:
      return null;
  }
}

// Helper function to get a random task for swapping
export function getRandomTask(packId: string, excludeIds: string[] = []): Task | null {
  const pack = getTaskPack(packId);
  if (!pack) return null;
  
  const availableTasks = pack.tasks.filter(task => !excludeIds.includes(task.id));
  if (availableTasks.length === 0) return null;
  
  const randomIndex = Math.floor(Math.random() * availableTasks.length);
  return availableTasks[randomIndex];
}

// Mock data for other packs (placeholders)
export const mockTasks = {
  corePackA,
  getRandomTasks,
  getTaskPack,
  getRandomTask
};
