import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import { TaskInstance, Player } from '../types';
import CardDeck from './CardDeck';
import TargetSelectModal from './TargetSelectModal';

export default function Game() {
  const { gameId } = useParams<{ gameId: string }>();
  const navigate = useNavigate();
  const { state, claimGotcha, disputeGotcha, acceptGotcha, forceGameState, simulateScore } = useGame();

  const [showLeaderboard, setShowLeaderboard] = useState(false);
  const [showTestingTools, setShowTestingTools] = useState(false);
  const [cardViewMode, setCardViewMode] = useState<'deck' | 'list'>('deck');
  const [showTargetModal, setShowTargetModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState<TaskInstance | null>(null);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);

  const currentGame = state.currentGame;
  const currentPlayer = state.currentPlayer;

  useEffect(() => {
    if (!currentGame || !currentPlayer) {
      navigate('/');
      return;
    }

    if (currentGame.status === 'draft') {
      navigate(`/lobby/${gameId}`);
    } else if (currentGame.status === 'ended') {
      navigate(`/recap/${gameId}`);
    }
  }, [currentGame, currentPlayer, navigate, gameId]);

  if (!currentGame || !currentPlayer) {
    return (
      <div className="container">
        <div className="card">
          <p>‚è≥ Loading game...</p>
        </div>
      </div>
    );
  }

  const getCurrentScore = () => {
    return currentPlayer.score || 0;
  };

  const getPendingTasks = () => {
    return currentPlayer.tasks.filter((task: TaskInstance) => task.status === 'pending');
  };

  const getCompletedTasks = () => {
    return currentPlayer.tasks.filter((task: TaskInstance) => task.status === 'got');
  };

  const getFailedTasks = () => {
    return currentPlayer.tasks.filter((task: TaskInstance) => task.status === 'failed');
  };

  const getAvailableTargets = (): Player[] => {
    return currentGame.players.filter(p => p.id !== currentPlayer.id);
  };

  const handleGotchaClick = (task: TaskInstance) => {
    if (task.status !== 'pending') return;
    setSelectedTask(task);
    setShowTargetModal(true);
  };

  const handleTargetSelect = async (targetId: string) => {
    if (!selectedTask) return;
    
    await claimGotcha(selectedTask.id, targetId);
    setShowTargetModal(false);
    setSelectedTask(null);
  };

  const handleSwapTask = (taskId: string) => {
    console.log('Swap task:', taskId);
  };

  // Create cards for the deck - just for cycling, not completing
  const createTaskCards = () => {
    const pendingTasks = getPendingTasks();
    
    return pendingTasks.map((task: TaskInstance, index: number) => ({
      id: task.id,
      content: (
        <div className="task-card-content">
          <div className="task-header">
            <h3>üéØ Your Mission</h3>
            <div className="difficulty-badge">
              <span>üé≤ Level {task.difficulty}</span>
            </div>
          </div>
          
          <div className="task-text">
            <p>{task.text}</p>
          </div>
          
          <div className="task-footer">
            <div style={{ marginBottom: '1rem' }}>
              <button
                className="btn btn-success"
                onClick={() => handleGotchaClick(task)}
                style={{ width: '100%' }}
              >
                üéØ Claim Gotcha!
              </button>
            </div>
            <div className="task-tips">
              <p className="text-small">üí° Swipe left/right to see other tasks ‚Ä¢ Tap "Claim Gotcha!" when complete</p>
            </div>
          </div>
        </div>
      ),
      leftAction: 'Previous',
      rightAction: 'Next',
      upAction: '',
      downAction: '',
      onSwipeLeft: () => {
        // Cycle to previous card
        const newIndex = index > 0 ? index - 1 : pendingTasks.length - 1;
        setCurrentCardIndex(newIndex);
      },
      onSwipeRight: () => {
        // Cycle to next card  
        const newIndex = index < pendingTasks.length - 1 ? index + 1 : 0;
        setCurrentCardIndex(newIndex);
      },
      onSwipeUp: undefined,
      onSwipeDown: undefined
    }));
  };

  const taskCards = createTaskCards();

  return (
    <div className="container">
      {/* Header */}
      <div style={{ marginBottom: '1rem' }}>
        <div className="flex flex-between" style={{ alignItems: 'center' }}>
          <div>
            <h1 style={{ marginBottom: '0.25rem', fontSize: '1.5rem' }}>Who Got Who</h1>
            <p className="text-small" style={{ color: 'rgba(255, 255, 255, 0.8)' }}>üéÆ Live ‚Ä¢ ID: <strong>{gameId}</strong></p>
          </div>
          <div className="flex" style={{ gap: '0.25rem' }}>
            <button 
              className="btn btn-small btn-secondary"
              onClick={() => setShowLeaderboard(!showLeaderboard)}
              style={{ padding: '6px 12px' }}
            >
              üèÜ
            </button>
            <button 
              className="btn btn-small"
              onClick={() => setCardViewMode(cardViewMode === 'deck' ? 'list' : 'deck')}
              style={{ padding: '6px 12px' }}
            >
              {cardViewMode === 'deck' ? 'üìã' : 'üé¥'}
            </button>
          </div>
        </div>
      </div>

      {state.error && (
        <div className="card card-warning">
          <p><strong>‚ùå {state.error}</strong></p>
        </div>
      )}

      {/* Player Status */}
      <div className="card">
        <div className="flex flex-between">
          <div>
            <h3>üë§ {currentPlayer.name}</h3>
            <p className="text-small">Your progress in this game</p>
          </div>
          <div className="text-center">
            <div className="score-badge" style={{ fontSize: '1.2rem', padding: '12px 20px' }}>
              {getCurrentScore()} pts
            </div>
            <div className="text-small" style={{ marginTop: '0.5rem' }}>
              Goal: {currentGame.settings.targetScore}
            </div>
          </div>
        </div>
      </div>

      {/* Leaderboard */}
      {showLeaderboard && (
        <div className="card">
          <h3>üèÜ Live Leaderboard</h3>
          {currentGame.players
            .sort((a: Player, b: Player) => (b.score || 0) - (a.score || 0))
            .map((player: Player, index: number) => (
              <div key={player.id} className="flex flex-between" style={{ 
                padding: '1rem', 
                borderRadius: '12px',
                margin: '0.5rem 0',
                background: player.id === currentPlayer.id 
                  ? 'rgba(255, 107, 107, 0.3)' 
                  : 'rgba(255, 255, 255, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.2)'
              }}>
                <div>
                  <span style={{ marginRight: '1rem', fontSize: '1.2rem' }}>
                    {index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : index === 2 ? 'ü•â' : `${index + 1}.`}
                  </span>
                  <strong>{player.name}</strong>
                  {player.id === currentPlayer.id && <span className="text-small"> üë§ (You)</span>}
                </div>
                <div className="score-badge">
                  {player.score || 0}
                </div>
              </div>
            ))}
        </div>
      )}

      {/* Task Cards */}
      {cardViewMode === 'deck' ? (
        <div className="card">
          <div className="card-header">
            <h3>üé¥ Your Task Cards</h3>
            <div className="text-small">Swipe left/right to cycle ‚Ä¢ Tap button to claim Gotcha!</div>
          </div>
          
          {taskCards.length > 0 ? (
            <CardDeck
              cards={taskCards}
              maxVisible={1}
              emptyState={
                <div className="empty-deck">
                  <div className="empty-deck-icon">üéâ</div>
                  <h3>All Tasks Complete!</h3>
                  <p>Great job! Check the leaderboard to see your standing.</p>
                </div>
              }
            />
          ) : (
            <div className="text-center" style={{ padding: '3rem' }}>
              <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üéâ</div>
              <h3>All Tasks Complete!</h3>
              <p className="text-small">You've finished all your tasks! Check the leaderboard.</p>
            </div>
          )}
        </div>
      ) : (
        /* List View */
        <div className="card">
          <div className="card-header">
            <h3>üìã Your Tasks</h3>
            <div className="text-small">
              Pending: {getPendingTasks().length} ‚Ä¢ 
              Completed: {getCompletedTasks().length} ‚Ä¢ 
              Failed: {getFailedTasks().length}
            </div>
          </div>

          {/* Pending Tasks */}
          {getPendingTasks().map((task: TaskInstance) => (
            <div key={task.id} className="task-item">
              <div style={{ marginBottom: '1rem' }}>
                <p><strong>{task.text}</strong></p>
                <div className="text-small" style={{ opacity: 0.8, marginTop: '0.5rem' }}>
                  üé≤ Difficulty: {task.difficulty}/5
                </div>
              </div>
              
              <div className="flex" style={{ gap: '0.5rem' }}>
                <button
                  className="btn btn-success"
                  onClick={() => handleGotchaClick(task)}
                  disabled={state.isLoading}
                  style={{ flex: 1 }}
                >
                  üéØ Claim Gotcha!
                </button>
                
                {currentPlayer.swapsLeft > 0 && (
                  <button
                    className="btn btn-small btn-secondary"
                    onClick={() => handleSwapTask(task.id)}
                    disabled={state.isLoading}
                  >
                    üîÑ Swap
                  </button>
                )}
              </div>
            </div>
          ))}

          {/* Completed Tasks */}
          {getCompletedTasks().map((task: TaskInstance) => (
            <div key={task.id} className="task-item completed">
              <div className="flex flex-between">
                <div>
                  <p><strong>{task.text}</strong></p>
                  <div className="text-small" style={{ opacity: 0.8, marginTop: '0.25rem' }}>
                    ‚úÖ Completed ‚Ä¢ +1 point
                  </div>
                </div>
                <div className="score-badge" style={{ background: 'linear-gradient(135deg, #56ab2f, #a8e6cf)' }}>
                  ‚úÖ Got
                </div>
              </div>
            </div>
          ))}

          {/* Failed Tasks */}
          {getFailedTasks().map((task: TaskInstance) => (
            <div key={task.id} className="task-item failed">
              <div className="flex flex-between">
                <div>
                  <p><strong>{task.text}</strong></p>
                  <div className="text-small" style={{ opacity: 0.8, marginTop: '0.25rem' }}>
                    ‚ùå Failed ‚Ä¢ No points
                  </div>
                </div>
                <div className="score-badge" style={{ background: 'linear-gradient(135deg, #ff416c, #ff4757)' }}>
                  ‚ùå Failed
                </div>
              </div>
            </div>
          ))}

          {currentPlayer.tasks.length === 0 && (
            <div className="text-center text-small" style={{ padding: '2rem', opacity: 0.7 }}>
              <p>‚è≥ No tasks assigned yet. Waiting for game to start...</p>
            </div>
          )}
        </div>
      )}

      {/* Game Status */}
      <div className="card card-demo">
        <h3>üìä Game Status</h3>
        <div className="text-small" style={{ lineHeight: '1.8' }}>
          <div className="flex flex-between" style={{ margin: '0.5rem 0' }}>
            <span>üéØ Your Score:</span>
            <strong>{getCurrentScore()} / {currentGame.settings.targetScore}</strong>
          </div>
          <div className="flex flex-between" style={{ margin: '0.5rem 0' }}>
            <span>üìã Tasks Remaining:</span>
            <strong>{getPendingTasks().length}</strong>
          </div>
          <div className="flex flex-between" style={{ margin: '0.5rem 0' }}>
            <span>üë• Total Players:</span>
            <strong>{currentGame.players.length}</strong>
          </div>
          <div className="flex flex-between" style={{ margin: '0.5rem 0' }}>
            <span>üéÆ Game Mode:</span>
            <strong style={{ color: currentGame.mode === 'casual' ? '#56ab2f' : '#ff416c' }}>
              {currentGame.mode === 'casual' ? 'üåü Casual' : '‚öîÔ∏è Competitive'}
            </strong>
          </div>
        </div>
      </div>

      {/* Testing Tools */}
      <div className="card card-test">
        <div className="flex flex-between" style={{ marginBottom: '1rem' }}>
          <h3>üß™ Demo Testing Tools</h3>
          <button 
            className="btn btn-small btn-secondary"
            onClick={() => setShowTestingTools(!showTestingTools)}
          >
            {showTestingTools ? 'üôà Hide' : 'üëÄ Show'} Tools
          </button>
        </div>
        
        {showTestingTools && (
          <div>
            <p className="text-small" style={{ marginBottom: '1rem' }}>
              Test game mechanics and scoring:
            </p>
            
            <div className="flex" style={{ gap: '0.5rem', flexWrap: 'wrap', marginBottom: '1rem' }}>
              <button
                className="btn btn-small btn-success"
                onClick={() => simulateScore?.(1)}
              >
                +1 Point
              </button>
              
              <button
                className="btn btn-small btn-danger"
                onClick={() => simulateScore?.(-1)}
              >
                -1 Point
              </button>
              
              <button
                className="btn btn-small"
                onClick={() => forceGameState?.('ended')}
                style={{ background: 'linear-gradient(135deg, #ffd700, #ffed4e)', color: '#000' }}
              >
                üèÜ End Game Now
              </button>
            </div>
            
            <p className="text-small">
              Current Score: {getCurrentScore()} | 
              Target: {currentGame.settings.targetScore} | 
              Tasks Left: {getPendingTasks().length}
            </p>
          </div>
        )}
      </div>

      {/* Target Selection Modal */}
      <TargetSelectModal
        isOpen={showTargetModal}
        onClose={() => {
          setShowTargetModal(false);
          setSelectedTask(null);
        }}
        onSelectTarget={handleTargetSelect}
        availableTargets={getAvailableTargets()}
        taskText={selectedTask?.text || ''}
      />
    </div>
  );
}
