import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import { Player, TaskInstance } from '../types';
import CardDeck from './CardDeck';

export default function Lobby() {
  const { gameId } = useParams<{ gameId: string }>();
  const navigate = useNavigate();
  const { state, lockInPlayer, swapTask, startGame, forceGameState, simulateScore } = useGame();
  const [selectedTaskId, setSelectedTaskId] = useState<string | null>(null);
  const [showPlayerList, setShowPlayerList] = useState(false);
  const [showTestingTools, setShowTestingTools] = useState(false);
  const [taskViewMode, setTaskViewMode] = useState<'deck' | 'list'>('list');

  const currentPlayer = state.currentPlayer;
  const currentGame = state.currentGame;
  const isHost = currentPlayer?.isHost || false;
  const allPlayersLocked = currentGame?.players.every(p => p.lockedIn) || false;
  const canStart = allPlayersLocked && (currentGame?.players.length || 0) >= 3;

  useEffect(() => {
    if (!gameId) {
      navigate('/');
      return;
    }

    // TODO: Join game if not already joined
    if (!currentGame || currentGame.id !== gameId) {
      // Auto-join logic here
    }
  }, [gameId, currentGame, navigate]);

  useEffect(() => {
    // Navigate to game when it starts
    if (currentGame?.status === 'live') {
      navigate(`/game/${gameId}`);
    }
  }, [currentGame?.status, gameId, navigate]);

  const handleLockIn = async () => {
    await lockInPlayer();
  };

  const handleSwapTask = async (taskId: string) => {
    if (!currentPlayer || currentPlayer.swapsLeft <= 0) {
      console.log('No swaps remaining');
      return;
    }
    
    await swapTask(taskId);
  };

  const handleStartGame = async () => {
    if (isHost && canStart) {
      await startGame();
    }
  };

  const getSwapButtonText = () => {
    if (!currentPlayer || currentPlayer.swapsLeft <= 0) return 'No swaps left';
    return `Swap (${currentPlayer.swapsLeft} left)`;
  };

  // Create cards for the deck
  const createTaskCards = () => {
    if (!currentPlayer) return [];
    
    return currentPlayer.tasks.map((task: TaskInstance) => ({
      id: task.id,
      content: (
        <div className="task-card-content">
          <div className="task-header">
            <h3>üìù Task Preview</h3>
            <div className="difficulty-badge">
              <span>üé≤ Level {task.difficulty}</span>
            </div>
          </div>
          
          <div className="task-text">
            <p>{task.text}</p>
          </div>
          
          <div className="task-footer">
            <div className="task-tips">
              <p className="text-small">
                {currentPlayer.swapsLeft > 0 
                  ? 'üí° Swipe down to swap this task ‚Ä¢ Swipe up to keep it'
                  : 'üîí No swaps remaining'
                }
              </p>
            </div>
          </div>
        </div>
      ),
      leftAction: currentPlayer.lockedIn ? '' : 'Skip',
      rightAction: currentPlayer.lockedIn ? '' : 'Keep',
      upAction: currentPlayer.lockedIn ? '' : 'Keep',
      downAction: currentPlayer.lockedIn || currentPlayer.swapsLeft <= 0 ? '' : 'Swap',
      onSwipeLeft: currentPlayer.lockedIn ? undefined : () => {
        // Skip - move to next card
        console.log('Skipped reviewing:', task.text);
      },
      onSwipeRight: currentPlayer.lockedIn ? undefined : () => {
        // Keep - move to next card
        console.log('Kept task:', task.text);
      },
      onSwipeUp: currentPlayer.lockedIn ? undefined : () => {
        // Keep - move to next card
        console.log('Kept task:', task.text);
      },
      onSwipeDown: (currentPlayer.lockedIn || currentPlayer.swapsLeft <= 0) ? undefined : () => {
        handleSwapTask(task.id);
      }
    }));
  };

  const taskCards = createTaskCards();

  if (!currentGame || !currentPlayer) {
    return (
      <div className="flex flex-center" style={{ minHeight: '100vh' }}>
        <div className="card">
          <h2>Loading...</h2>
          <p>Connecting to game...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      {/* Header */}
      <div className="card-header">
        <div className="flex flex-between">
          <div>
            <h1>üéÆ Game Lobby</h1>
            <p className="text-small">ID: <strong>{gameId}</strong></p>
          </div>
          <div className="flex" style={{ gap: '0.5rem' }}>
            <button 
              className="btn btn-small btn-secondary"
              onClick={() => setShowPlayerList(!showPlayerList)}
            >
              üë• Players ({currentGame.players.length})
            </button>
            {!currentPlayer.lockedIn && (
              <button 
                className="btn btn-small"
                onClick={() => setTaskViewMode(taskViewMode === 'deck' ? 'list' : 'deck')}
              >
                {taskViewMode === 'deck' ? 'üìã List' : 'üé¥ Deck'}
              </button>
            )}
          </div>
        </div>
      </div>

      {state.error && (
        <div className="card card-warning">
          <p><strong>‚ùå {state.error}</strong></p>
        </div>
      )}

      {/* Player List */}
      {showPlayerList && (
        <div className="card">
          <h3>üë• Players</h3>
          {currentGame.players.map((player: Player) => (
            <div key={player.id} className="flex flex-between" style={{ 
              padding: '1rem', 
              borderRadius: '12px',
              margin: '0.5rem 0',
              background: player.isHost ? 'rgba(255, 107, 107, 0.2)' : 'rgba(255, 255, 255, 0.1)',
              border: '1px solid rgba(255, 255, 255, 0.2)',
              transition: 'all 0.3s ease'
            }}>
              <div>
                <strong>{player.name}</strong>
                {player.isHost && <span className="text-small"> üëë Host</span>}
              </div>
              <div>
                {player.lockedIn ? (
                  <span className="score-badge" style={{ background: 'linear-gradient(135deg, #56ab2f, #a8e6cf)' }}>
                    ‚úÖ Ready
                  </span>
                ) : (
                  <span className="score-badge" style={{ background: 'linear-gradient(135deg, #ffd93d, #ff6b6b)' }}>
                    ‚è≥ Waiting...
                  </span>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Game Mode Info */}
      <div className="card card-demo">
        <h3>üéØ Game Mode: {currentGame.mode === 'casual' ? 'üåü Casual' : '‚öîÔ∏è Competitive'}</h3>
        <div className="text-small" style={{ lineHeight: '1.7' }}>
          <p>üîÑ Swaps allowed: <strong>{currentGame.settings.swapsAllowed}</strong></p>
          <p>üéØ Target score: <strong>{currentGame.settings.targetScore}</strong></p>
          <p>‚è∞ Dispute timeout: <strong>{currentGame.settings.disputeTimeoutSeconds}s</strong></p>
          {currentGame.settings.enableNegativeScoring && (
            <p>‚öñÔ∏è Negative scoring enabled</p>
          )}
        </div>
      </div>

      {/* Your Tasks */}
      {taskViewMode === 'deck' && !currentPlayer.lockedIn ? (
        <div className="card">
          <div className="card-header">
            <h3>üé¥ Review Your Tasks</h3>
            <div className="text-small">
              Swipe through to review ‚Ä¢ Down: Swap ({currentPlayer.swapsLeft} left) ‚Ä¢ Up/Right: Keep
            </div>
          </div>
          
          <CardDeck
            cards={taskCards}
            maxVisible={3}
            emptyState={
              <div className="empty-deck">
                <div className="empty-deck-icon">‚úÖ</div>
                <h3>All Tasks Reviewed!</h3>
                <p>Ready to lock in and start the game.</p>
              </div>
            }
          />
        </div>
      ) : (
        <div className="card">
          <div className="card-header">
            <h3>üìã Your Tasks</h3>
            <div className="text-small">
              Complete these stealth tasks on other players
            </div>
          </div>

          {currentPlayer.tasks.map((task: TaskInstance) => (
            <div key={task.id} className="task-item">
              <div style={{ marginBottom: '1rem' }}>
                <p><strong>{task.text}</strong></p>
                <div className="text-small" style={{ opacity: 0.8 }}>
                  üé≤ Difficulty: {task.difficulty}/5
                </div>
              </div>
              
              {!currentPlayer.lockedIn && currentPlayer.swapsLeft > 0 && (
                <button
                  className="btn btn-small btn-secondary"
                  onClick={() => handleSwapTask(task.id)}
                  disabled={state.isLoading}
                >
                  üîÑ {getSwapButtonText()}
                </button>
              )}
            </div>
          ))}

          {currentPlayer.tasks.length === 0 && (
            <div className="text-center text-small" style={{ padding: '2rem', opacity: 0.7 }}>
              <p>‚è≥ No tasks assigned yet. Waiting for game to distribute tasks...</p>
            </div>
          )}
        </div>
      )}

      {/* Lock In Section */}
      <div className="card">
        {!currentPlayer.lockedIn ? (
          <div>
            <h3>üöÄ Ready to Start?</h3>
            <p className="text-small" style={{ marginBottom: '1.5rem' }}>
              Lock in your tasks when you're happy with them. 
              You won't be able to swap after locking in.
            </p>
            <button
              className="btn btn-success"
              onClick={handleLockIn}
              disabled={state.isLoading}
            >
              {state.isLoading ? 'üîÑ Locking in...' : '‚úÖ Lock In & Ready Up'}
            </button>
          </div>
        ) : (
          <div className="text-center">
            <h3>‚úÖ You're Ready!</h3>
            <p className="text-small">
              ‚è≥ Waiting for other players to lock in...
            </p>
            <div className="pulse" style={{ 
              background: 'linear-gradient(135deg, #56ab2f, #a8e6cf)',
              padding: '1rem',
              borderRadius: '15px',
              margin: '1rem 0',
              color: '#000',
              fontWeight: 'bold'
            }}>
              üéØ Ready to Play!
            </div>
          </div>
        )}
      </div>

      {/* Host Controls */}
      {isHost && (
        <div className="card" style={{ background: 'linear-gradient(135deg, rgba(255, 107, 107, 0.2), rgba(238, 90, 111, 0.2))', border: '1px solid rgba(255, 107, 107, 0.4)' }}>
          <h3>üëë Host Controls</h3>
          
          {!allPlayersLocked && (
            <p className="text-small" style={{ marginBottom: '1rem', opacity: 0.9 }}>
              ‚è≥ Waiting for all players to lock in their tasks...
            </p>
          )}
          
          <button
            className="btn"
            onClick={handleStartGame}
            disabled={!canStart || state.isLoading}
            style={{
              background: canStart 
                ? 'linear-gradient(135deg, #56ab2f, #a8e6cf)' 
                : 'linear-gradient(135deg, #6c757d, #495057)'
            }}
          >
            {state.isLoading ? 'üîÑ Starting...' : 
             !canStart ? `‚è≥ Need ${Math.max(0, 3 - (currentGame?.players.length || 0))} more players` :
             'üöÄ Start Game!'}
          </button>

          {allPlayersLocked && !canStart && (
            <p className="text-small" style={{ marginTop: '1rem', textAlign: 'center' }}>
              ‚ö†Ô∏è Need at least 3 players to start
            </p>
          )}
        </div>
      )}

      {/* Share Game */}
      <div className="card">
        <h3>üì§ Invite Players</h3>
        <p className="text-small" style={{ marginBottom: '1rem' }}>Share this game ID:</p>
        <div className="flex" style={{ gap: '0.5rem', alignItems: 'center', marginBottom: '1rem' }}>
          <input
            type="text"
            className="input"
            value={gameId || ''}
            readOnly
            style={{ flexGrow: 1, textAlign: 'center', fontWeight: 'bold', fontSize: '1.2rem' }}
          />
          <button
            className="btn btn-small"
            onClick={() => navigator.clipboard?.writeText(gameId || '')}
          >
            üìã Copy
          </button>
        </div>
        
        <button
          className="btn btn-secondary"
          onClick={() => {
            const url = `${window.location.origin}/lobby/${gameId}`;
            navigator.clipboard?.writeText(url);
          }}
        >
          üîó Copy Join Link
        </button>
      </div>

      {/* Testing Tools */}
      <div className="card card-test">
        <div className="flex flex-between" style={{ marginBottom: '1rem' }}>
          <h3>üß™ Demo Testing Tools</h3>
          <button 
            className="btn btn-small btn-secondary"
            onClick={() => setShowTestingTools(!showTestingTools)}
          >
            {showTestingTools ? 'üôà Hide' : 'üëÄ Show'} Tools
          </button>
        </div>
        
        {showTestingTools && (
          <div>
            <p className="text-small" style={{ marginBottom: '1rem' }}>
              Skip ahead to test different game screens:
            </p>
            
            <div className="flex" style={{ gap: '0.5rem', flexWrap: 'wrap', marginBottom: '1rem' }}>
              <button
                className="btn btn-small"
                onClick={() => forceGameState?.('live')}
                style={{ background: 'linear-gradient(135deg, #4ecdc4, #44a08d)' }}
              >
                üéÆ Skip to Live Game
              </button>
              
              <button
                className="btn btn-small"
                onClick={() => {
                  simulateScore?.(3);
                  forceGameState?.('ended');
                }}
                style={{ background: 'linear-gradient(135deg, #ffd700, #ffed4e)', color: '#000' }}
              >
                üèÜ Skip to Game End
              </button>
            </div>
            
            <p className="text-small">
              <strong>üí° Tip:</strong> To test multiplayer views, open incognito windows 
              and join this game using ID: <strong style={{ color: '#ffd93d' }}>{gameId}</strong>
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
