import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import { TaskInstance, Player } from '../types';
import CardDeck from './CardDeck';
import TargetSelectModal from './TargetSelectModal';

export default function Game() {
  const { gameId } = useParams<{ gameId: string }>();
  const navigate = useNavigate();
  const { state, claimGotcha, disputeGotcha, acceptGotcha, forceGameState, simulateScore } = useGame();

  const [showLeaderboard, setShowLeaderboard] = useState(false);
  const [showTestingTools, setShowTestingTools] = useState(false);
  const [cardViewMode, setCardViewMode] = useState<'deck' | 'list'>('deck');
  const [showTargetModal, setShowTargetModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState<TaskInstance | null>(null);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);

  const currentGame = state.currentGame;
  const currentPlayer = state.currentPlayer;

  useEffect(() => {
    if (!currentGame || !currentPlayer) {
      navigate('/');
      return;
    }

    if (currentGame.status === 'draft') {
      navigate(`/lobby/${gameId}`);
    } else if (currentGame.status === 'ended') {
      navigate(`/recap/${gameId}`);
    }
  }, [currentGame, currentPlayer, navigate, gameId]);

  if (!currentGame || !currentPlayer) {
    return (
      <div className="container">
        <div className="card">
          <p>‚è≥ Loading game...</p>
        </div>
      </div>
    );
  }

  const getCurrentScore = () => {
    return currentPlayer.score || 0;
  };

  const getPendingTasks = () => {
    return currentPlayer.tasks.filter((task: TaskInstance) => task.status === 'pending');
  };

  const getCompletedTasks = () => {
    return currentPlayer.tasks.filter((task: TaskInstance) => task.status === 'got');
  };

  const getFailedTasks = () => {
    return currentPlayer.tasks.filter((task: TaskInstance) => task.status === 'failed');
  };

  const getAvailableTargets = (): Player[] => {
    return currentGame.players.filter(p => p.id !== currentPlayer.id);
  };

  const handleGotchaClick = (task: TaskInstance) => {
    if (task.status !== 'pending') return;
    setSelectedTask(task);
    setShowTargetModal(true);
  };

  const handleTargetSelect = async (targetId: string) => {
    if (!selectedTask) return;
    
    await claimGotcha(selectedTask.id, targetId);
    setShowTargetModal(false);
    setSelectedTask(null);
  };

  const handleSwapTask = (taskId: string) => {
    console.log('Swap task:', taskId);
  };

  // Create cards for the deck - just for cycling, not completing
  const createTaskCards = () => {
    const pendingTasks = getPendingTasks();
    
    return pendingTasks.map((task: TaskInstance, index: number) => ({
      id: task.id,
      content: (
        <div className="task-card-content">
          <div className="task-header">
            <h3>üéØ Your Mission</h3>
            <div className="difficulty-badge">
              <span>üé≤ Level {task.difficulty}</span>
            </div>
          </div>
          
          <div className="task-text">
            <p>{task.text}</p>
          </div>
          
          <div className="task-footer">
            <div style={{ marginBottom: '1rem' }}>
              <button
                className="btn btn-success"
                onClick={() => handleGotchaClick(task)}
                style={{ width: '100%' }}
              >
                üéØ Claim Gotcha!
              </button>
            </div>
            <div className="task-tips">
              <p className="text-small">üí° Swipe left/right to see other tasks ‚Ä¢ Tap "Claim Gotcha!" when complete</p>
            </div>
          </div>
        </div>
      ),
      leftAction: 'Previous',
      rightAction: 'Next',
      upAction: '',
      downAction: '',
      onSwipeLeft: () => {
        // Cycle to previous card
        const newIndex = index > 0 ? index - 1 : pendingTasks.length - 1;
        setCurrentCardIndex(newIndex);
      },
      onSwipeRight: () => {
        // Cycle to next card  
        const newIndex = index < pendingTasks.length - 1 ? index + 1 : 0;
        setCurrentCardIndex(newIndex);
      },
      onSwipeUp: undefined,
      onSwipeDown: undefined
    }));
  };

  const taskCards = createTaskCards();

  const endGame = () => {
    if (currentGame.status === 'live') {
      forceGameState?.('ended');
    }
  };

  return (
    <div className="container">
      {/* Compact Header */}
      <div style={{ 
        textAlign: 'center', 
        marginBottom: '1.5rem',
        padding: '1rem 0'
      }}>
        <h1 style={{ 
          fontSize: '2rem', 
          marginBottom: '0.5rem',
          color: 'white',
          textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)',
          fontWeight: '900',
          textTransform: 'uppercase'
        }}>
          WHO GOT WHO
        </h1>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center', 
          gap: '1rem',
          flexWrap: 'wrap'
        }}>
          <div style={{ 
            background: 'rgba(255, 255, 255, 0.2)',
            backdropFilter: 'blur(10px)',
            padding: '0.5rem 1rem',
            borderRadius: '20px',
            color: 'white',
            fontWeight: '700',
            fontSize: '0.9rem',
            textTransform: 'uppercase',
            letterSpacing: '0.5px'
          }}>
            üèÜ {currentPlayer?.score || 0} Points
          </div>
          <button
            className="btn btn-small"
            onClick={() => setCardViewMode(cardViewMode === 'deck' ? 'list' : 'deck')}
            style={{ 
              background: 'rgba(255, 255, 255, 0.25)',
              color: 'white',
              border: '2px solid rgba(255, 255, 255, 0.3)',
              minHeight: '36px',
              fontSize: '0.8rem'
            }}
          >
            {cardViewMode === 'deck' ? 'üìù' : 'üé¥'}
          </button>
        </div>
      </div>

      {/* Game Status */}
      <div className="card" style={{ 
        background: 'linear-gradient(135deg, #E8F5E8, #C8E6C9)',
        border: '2px solid rgba(76, 175, 80, 0.3)',
        color: '#2E7D32',
        marginBottom: '1rem'
      }}>
        <div className="flex flex-between">
          <div>
            <h3 style={{ 
              fontSize: '1.2rem', 
              marginBottom: '0.5rem',
              textTransform: 'uppercase',
              fontWeight: '800'
            }}>
              üéÆ Live Game
            </h3>
            <div style={{ fontWeight: '600' }}>
              <div>üéØ <strong>Target:</strong> 4 Gotchas to win</div>
              <div>üë• <strong>Players:</strong> {currentGame.players.length}</div>
            </div>
          </div>
          <div style={{ 
            background: 'rgba(46, 125, 50, 0.1)',
            padding: '1rem',
            borderRadius: '15px',
            textAlign: 'center',
            minWidth: '80px'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: '800' }}>
              {getPendingTasks().length}
            </div>
            <div style={{ fontSize: '0.7rem', textTransform: 'uppercase', fontWeight: '700' }}>
              Tasks Left
            </div>
          </div>
        </div>
      </div>

      {/* Leaderboard */}
      <div className="card">
        <h3 style={{ 
          fontSize: '1.2rem', 
          marginBottom: '1rem',
          textTransform: 'uppercase',
          fontWeight: '800',
          color: '#4A90E2'
        }}>
          üèÜ Leaderboard
        </h3>
        <div>
          {currentGame.players
            .sort((a, b) => b.score - a.score)
            .map((player, index) => (
              <div 
                key={player.id}
                style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  padding: '1rem',
                  marginBottom: '0.5rem',
                  background: index === 0 
                    ? 'linear-gradient(135deg, #FFD700, #FFC107)' 
                    : player.id === currentPlayer?.id
                    ? 'linear-gradient(135deg, #E3F2FD, #BBDEFB)'
                    : 'linear-gradient(135deg, #F5F5F5, #EEEEEE)',
                  border: index === 0 
                    ? '2px solid rgba(255, 193, 7, 0.5)' 
                    : player.id === currentPlayer?.id
                    ? '2px solid rgba(33, 150, 243, 0.3)'
                    : '2px solid rgba(0, 0, 0, 0.1)',
                  borderRadius: '15px',
                  color: index === 0 ? '#333333' : player.id === currentPlayer?.id ? '#1976D2' : '#333333',
                  fontWeight: '600',
                  transition: 'all 0.3s ease'
                }}
              >
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <span style={{ 
                    fontSize: '1.2rem',
                    fontWeight: '800',
                    minWidth: '24px'
                  }}>
                    {index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : index === 2 ? 'ü•â' : `${index + 1}.`}
                  </span>
                  <span style={{ 
                    fontWeight: '700',
                    fontSize: player.id === currentPlayer?.id ? '1.1rem' : '1rem'
                  }}>
                    {player.name}
                  </span>
                  {player.isHost && <span style={{ fontSize: '0.8rem' }}>üëë</span>}
                  {player.id === currentPlayer?.id && (
                    <span style={{ 
                      background: 'rgba(0, 0, 0, 0.1)',
                      padding: '0.25rem 0.5rem',
                      borderRadius: '10px',
                      fontSize: '0.7rem',
                      fontWeight: '700',
                      textTransform: 'uppercase'
                    }}>
                      YOU
                    </span>
                  )}
                </div>
                <div style={{ 
                  fontSize: '1.2rem',
                  fontWeight: '800',
                  background: 'rgba(0, 0, 0, 0.1)',
                  padding: '0.5rem 1rem',
                  borderRadius: '20px',
                  minWidth: '60px',
                  textAlign: 'center'
                }}>
                  {player.score}
                </div>
              </div>
          ))}
        </div>
      </div>

      {/* Pending Tasks */}
      {currentPlayer && getPendingTasks().length > 0 && (
        <div className="card">
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: '1rem'
          }}>
            <h3 style={{ 
              fontSize: '1.2rem', 
              margin: 0,
              textTransform: 'uppercase',
              fontWeight: '800',
              color: '#4A90E2'
            }}>
              üéØ Your Tasks
            </h3>
            <div style={{
              background: 'linear-gradient(135deg, #FF9800, #FFB74D)',
              color: 'white',
              padding: '0.5rem 1rem',
              borderRadius: '20px',
              fontSize: '0.8rem',
              fontWeight: '800',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              {getPendingTasks().length} Left
            </div>
          </div>

          {cardViewMode === 'deck' ? (
            <CardDeck
              cards={createTaskCards()}
              maxVisible={1}
              emptyState={
                <div className="empty-deck">
                  <div className="empty-deck-icon">üéâ</div>
                  <p style={{ fontWeight: '600' }}>All tasks completed!</p>
                </div>
              }
            />
          ) : (
            <div>
              {getPendingTasks().map(task => (
                <div key={task.id} className="task-item">
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'flex-start',
                    marginBottom: '0.5rem'
                  }}>
                    <div style={{ flex: 1 }}>
                      <p style={{ 
                        margin: 0, 
                        marginBottom: '0.5rem',
                        fontWeight: '600',
                        fontSize: '1rem'
                      }}>
                        {task.text}
                      </p>
                      <div style={{ 
                        fontSize: '0.8rem',
                        color: '#666',
                        fontWeight: '600'
                      }}>
                        Difficulty: <span style={{ textTransform: 'uppercase' }}>{task.difficulty}</span>
                      </div>
                    </div>
                    <button
                      className="btn btn-small"
                      onClick={() => handleGotchaClick(task)}
                      style={{ 
                        marginLeft: '1rem',
                        minWidth: '100px',
                        fontSize: '0.7rem',
                        background: 'linear-gradient(135deg, #FF6B6B, #FF5252)'
                      }}
                    >
                      üéØ Gotcha!
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Completed Tasks */}
      {currentPlayer && getCompletedTasks().length > 0 && (
        <div className="card card-success">
          <h3 style={{ 
            fontSize: '1.2rem', 
            marginBottom: '1rem',
            textTransform: 'uppercase',
            fontWeight: '800'
          }}>
            ‚úÖ Completed Tasks ({getCompletedTasks().length})
          </h3>
          {getCompletedTasks().slice(0, 3).map(task => (
            <div key={task.id} className="task-item completed">
              <div style={{ fontWeight: '600' }}>
                <div style={{ marginBottom: '0.5rem' }}>
                  {task.text}
                </div>
                <div style={{ 
                  fontSize: '0.8rem',
                  color: '#2E7D32',
                  fontWeight: '700'
                }}>
                  ‚úÖ Got {currentGame.players.find(p => p.id === task.targetId)?.name || 'someone'}!
                </div>
              </div>
            </div>
          ))}
          {getCompletedTasks().length > 3 && (
            <div style={{ 
              textAlign: 'center',
              padding: '1rem',
              color: '#2E7D32',
              fontWeight: '700'
            }}>
              ... and {getCompletedTasks().length - 3} more! üéâ
            </div>
          )}
        </div>
      )}

      {/* Failed Tasks */}
      {currentPlayer && getFailedTasks().length > 0 && (
        <div className="card card-warning">
          <h3 style={{ 
            fontSize: '1.2rem', 
            marginBottom: '1rem',
            textTransform: 'uppercase',
            fontWeight: '800'
          }}>
            ‚ùå Failed Tasks ({getFailedTasks().length})
          </h3>
          {getFailedTasks().slice(0, 2).map(task => (
            <div key={task.id} className="task-item failed">
              <div style={{ fontWeight: '600' }}>
                <div style={{ marginBottom: '0.5rem' }}>
                  {task.text}
                </div>
                <div style={{ 
                  fontSize: '0.8rem',
                  color: '#C62828',
                  fontWeight: '700'
                }}>
                  ‚ùå Caught or disputed
                </div>
              </div>
            </div>
          ))}
          {getFailedTasks().length > 2 && (
            <div style={{ 
              textAlign: 'center',
              padding: '1rem',
              color: '#C62828',
              fontWeight: '700'
            }}>
              ... and {getFailedTasks().length - 2} more
            </div>
          )}
        </div>
      )}

      {/* Testing Tools */}
      <div className="card card-test">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginBottom: '1rem'
        }}>
          <h3 style={{ 
            fontSize: '1rem', 
            margin: 0,
            textTransform: 'uppercase',
            fontWeight: '800'
          }}>
            üîß Demo Tools
          </h3>
          <button
            className="btn btn-small"
            onClick={() => setShowTestingTools(!showTestingTools)}
            style={{ 
              background: 'rgba(123, 31, 162, 0.2)',
              color: '#7B1FA2',
              border: '2px solid rgba(123, 31, 162, 0.3)',
              minHeight: '32px',
              fontSize: '0.7rem'
            }}
          >
            {showTestingTools ? '‚ñº' : '‚ñ∂'}
          </button>
        </div>
        
        {showTestingTools && (
          <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
            <button
              className="btn btn-secondary btn-small"
              onClick={() => simulateScore?.(1)}
              style={{ fontSize: '0.7rem' }}
            >
              +1 Point
            </button>
            <button
              className="btn btn-danger btn-small"
              onClick={() => simulateScore?.(-1)}
              style={{ fontSize: '0.7rem' }}
            >
              -1 Point
            </button>
            <button
              className="btn btn-secondary btn-small"
              onClick={endGame}
              style={{ fontSize: '0.7rem' }}
            >
              üèÅ End Game
            </button>
          </div>
        )}
      </div>

      {/* Target Select Modal */}
      <TargetSelectModal
        isOpen={showTargetModal}
        onClose={() => {
          setShowTargetModal(false);
          setSelectedTask(null);
        }}
        onSelectTarget={handleTargetSelect}
        availableTargets={getAvailableTargets()}
        taskText={selectedTask?.text || ''}
      />
    </div>
  );
}
