import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import { TaskInstance, Player, Dispute } from '../types';
import TargetSelectModal from './TargetSelectModal';
import DisputeModal from './DisputeModal';
import VotingBanner from './VotingBanner';

export default function Game() {
  const { gameId } = useParams<{ gameId: string }>();
  const navigate = useNavigate();
  const { state, claimGotcha, disputeGotcha, acceptGotcha, forceGameState, simulateScore } = useGame();
  
  const [selectedTask, setSelectedTask] = useState<TaskInstance | null>(null);
  const [showTargetModal, setShowTargetModal] = useState(false);
  const [showDisputeModal, setShowDisputeModal] = useState(false);
  const [currentDispute, setCurrentDispute] = useState<Dispute | null>(null);
  const [showLeaderboard, setShowLeaderboard] = useState(false);
  const [showTestingTools, setShowTestingTools] = useState(false);

  const currentPlayer = state.currentPlayer;
  const currentGame = state.currentGame;

  useEffect(() => {
    if (!gameId || !currentGame) {
      navigate('/');
      return;
    }

    if (currentGame.status === 'ended') {
      navigate(`/recap/${gameId}`);
    }
  }, [gameId, currentGame, navigate]);

  // Handle incoming disputes
  useEffect(() => {
    const activeDispute = state.activeDisputes.find(d => 
      d.targetId === currentPlayer?.id && d.result === 'pending'
    );
    
    if (activeDispute && !currentDispute) {
      setCurrentDispute(activeDispute);
      setShowDisputeModal(true);
    }
  }, [state.activeDisputes, currentPlayer?.id, currentDispute]);

  const handleGotchaClick = (task: TaskInstance) => {
    if (task.status !== 'pending') return;
    setSelectedTask(task);
    setShowTargetModal(true);
  };

  const handleTargetSelect = async (targetId: string) => {
    if (!selectedTask) return;
    
    await claimGotcha(selectedTask.id, targetId);
    setShowTargetModal(false);
    setSelectedTask(null);
  };

  const handleAcceptDispute = async () => {
    if (currentDispute) {
      await acceptGotcha(currentDispute.taskInstanceId);
      setShowDisputeModal(false);
      setCurrentDispute(null);
    }
  };

  const handleDispute = async (reason?: string) => {
    if (currentDispute) {
      await disputeGotcha(currentDispute.taskInstanceId, reason);
      setShowDisputeModal(false);
      setCurrentDispute(null);
    }
  };

  const getTaskStatusColor = (status: string) => {
    switch (status) {
      case 'got': return '#28a745';
      case 'failed': return '#dc3545';
      case 'disputed': return '#ffc107';
      default: return '#007bff';
    }
  };

  const getTaskStatusText = (status: string) => {
    switch (status) {
      case 'got': return '✓ Completed';
      case 'failed': return '✗ Failed';
      case 'disputed': return '⚖️ Disputed';
      default: return 'Pending';
    }
  };

  const getPendingTasks = () => {
    return currentPlayer?.tasks.filter(t => t.status === 'pending') || [];
  };

  const getCompletedTasks = () => {
    return currentPlayer?.tasks.filter(t => t.status !== 'pending') || [];
  };

  const getCurrentScore = () => {
    return currentPlayer?.score || 0;
  };

  const getLeaderboard = () => {
    return [...(currentGame?.players || [])].sort((a, b) => b.score - a.score);
  };

  if (!currentGame || !currentPlayer) {
    return (
      <div className="flex flex-center" style={{ minHeight: '100vh' }}>
        <div className="card">
          <h2>Loading...</h2>
          <p>Connecting to game...</p>
        </div>
      </div>
    );
  }

  const pendingTasks = getPendingTasks();
  const completedTasks = getCompletedTasks();
  const leaderboard = getLeaderboard();

  return (
    <div className="container">
      {/* Header */}
      <div className="card-header">
        <div className="flex flex-between">
          <div>
            <h1>Who Got Who</h1>
            <p className="text-small">Game: {gameId}</p>
          </div>
          <div className="flex" style={{ gap: '0.5rem' }}>
            <div className="score-badge">
              Score: {getCurrentScore()}
            </div>
            <button 
              className="btn btn-small btn-secondary"
              onClick={() => setShowLeaderboard(!showLeaderboard)}
            >
              Leaderboard
            </button>
          </div>
        </div>
      </div>

      {state.error && (
        <div className="card" style={{ backgroundColor: '#dc3545' }}>
          <p>{state.error}</p>
        </div>
      )}

      {/* Voting Banners */}
      {state.activeDisputes
        .filter(d => d.result === 'pending' && d.targetId !== currentPlayer.id)
        .map(dispute => (
          <VotingBanner key={dispute.id} dispute={dispute} />
        ))}

      {/* Leaderboard */}
      {showLeaderboard && (
        <div className="card">
          <h3>Leaderboard</h3>
          {leaderboard.map((player: Player, index: number) => (
            <div key={player.id} className="flex flex-between" style={{ 
              padding: '0.5rem', 
              borderBottom: '1px solid #444',
              backgroundColor: player.id === currentPlayer.id ? '#333' : 'transparent'
            }}>
              <div>
                <span style={{ marginRight: '0.5rem' }}>
                  {index === 0 ? '🥇' : index === 1 ? '🥈' : index === 2 ? '🥉' : `${index + 1}.`}
                </span>
                <strong>{player.name}</strong>
                {player.id === currentPlayer.id && <span className="text-small"> (You)</span>}
              </div>
              <div className="score-badge">
                {player.score}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Pending Tasks */}
      <div className="card">
        <div className="card-header">
          <h3>Your Tasks ({pendingTasks.length} remaining)</h3>
          <div className="text-small">
            Complete tasks and say "Gotcha!" out loud
          </div>
        </div>

        {pendingTasks.length === 0 ? (
          <div className="text-center" style={{ padding: '2rem' }}>
            <h3>🎉 All tasks completed!</h3>
            <p className="text-small">
              Wait for the game to end or other players to finish
            </p>
          </div>
        ) : (
          pendingTasks.map((task: TaskInstance) => (
            <div key={task.id} className="task-item">
              <div style={{ marginBottom: '1rem' }}>
                <p><strong>{task.text}</strong></p>
                <div className="text-small">
                  Difficulty: {task.difficulty}/5
                </div>
              </div>
              
              <button
                className="btn btn-success"
                onClick={() => handleGotchaClick(task)}
                disabled={state.isLoading}
              >
                🎯 Gotcha!
              </button>
            </div>
          ))
        )}
      </div>

      {/* Completed Tasks */}
      {completedTasks.length > 0 && (
        <div className="card">
          <h3>Completed Tasks</h3>
          {completedTasks.map((task: TaskInstance) => (
            <div 
              key={task.id} 
              className={`task-item ${task.status}`}
              style={{ 
                borderLeftColor: getTaskStatusColor(task.status),
                opacity: 0.8 
              }}
            >
              <div style={{ marginBottom: '0.5rem' }}>
                <p><strong>{task.text}</strong></p>
                <div className="text-small">
                  {getTaskStatusText(task.status)}
                  {task.targetId && (
                    <span> • Target: {
                      currentGame?.players.find(p => p.id === task.targetId)?.name || 'Unknown'
                    }</span>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Game Status */}
      <div className="card">
        <h3>Game Status</h3>
        <div className="text-small">
          <p>Players: {currentGame.players.length}</p>
          <p>Mode: {currentGame.mode}</p>
          <p>Target Score: {currentGame.settings.targetScore}</p>
          <div style={{ marginTop: '1rem' }}>
            <p><strong>Win Condition:</strong></p>
            <p>First to {currentGame.settings.targetScore} points wins!</p>
          </div>
        </div>
      </div>

      {/* Testing Tools */}
      <div className="card" style={{ backgroundColor: '#2d4a87' }}>
        <div className="flex flex-between" style={{ marginBottom: '1rem' }}>
          <h3>🧪 Demo Testing Tools</h3>
          <button 
            className="btn btn-small btn-secondary"
            onClick={() => setShowTestingTools(!showTestingTools)}
          >
            {showTestingTools ? 'Hide' : 'Show'} Tools
          </button>
        </div>
        
        {showTestingTools && (
          <div>
            <p className="text-small" style={{ marginBottom: '1rem' }}>
              Test game mechanics and scoring:
            </p>
            
            <div className="flex" style={{ gap: '0.5rem', flexWrap: 'wrap', marginBottom: '1rem' }}>
              <button
                className="btn btn-small btn-success"
                onClick={() => simulateScore?.(1)}
              >
                +1 Point
              </button>
              
              <button
                className="btn btn-small btn-danger"
                onClick={() => simulateScore?.(-1)}
              >
                -1 Point
              </button>
              
              <button
                className="btn btn-small"
                onClick={() => forceGameState?.('ended')}
              >
                🏆 End Game Now
              </button>
            </div>
            
            <p className="text-small">
              Current Score: {getCurrentScore()} | 
              Target: {currentGame.settings.targetScore} | 
              Tasks Left: {getPendingTasks().length}
            </p>
          </div>
        )}
      </div>

      {/* Modals */}
      {showTargetModal && selectedTask && (
        <TargetSelectModal
          task={selectedTask}
          players={currentGame.players.filter(p => p.id !== currentPlayer.id)}
          onSelectTarget={handleTargetSelect}
          onCancel={() => {
            setShowTargetModal(false);
            setSelectedTask(null);
          }}
        />
      )}

      {showDisputeModal && currentDispute && (
        <DisputeModal
          dispute={currentDispute}
          onAccept={handleAcceptDispute}
          onDispute={handleDispute}
          onCancel={() => {
            setShowDisputeModal(false);
            setCurrentDispute(null);
          }}
        />
      )}
    </div>
  );
}
