import React, { useState, useEffect } from 'react';
import SwipeableCard from './SwipeableCard';

interface CardDeckProps {
  cards: Array<{
    id: string;
    content: React.ReactNode;
    leftAction?: string;
    rightAction?: string;
    upAction?: string;
    downAction?: string;
    onSwipeLeft?: () => void;
    onSwipeRight?: () => void;
    onSwipeUp?: () => void;
    onSwipeDown?: () => void;
  }>;
  maxVisible?: number;
  className?: string;
  onCardRemoved?: (cardId: string) => void;
  emptyState?: React.ReactNode;
}

export default function CardDeck({
  cards,
  maxVisible = 3,
  className = '',
  onCardRemoved,
  emptyState
}: CardDeckProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [removedCards, setRemovedCards] = useState<Set<string>>(new Set());
  const [animatingOut, setAnimatingOut] = useState<string | null>(null);

  const availableCards = cards.filter(card => !removedCards.has(card.id));
  const visibleCards = availableCards.slice(currentIndex, currentIndex + maxVisible);

  const removeCard = (cardId: string, direction: 'left' | 'right' | 'up' | 'down') => {
    setAnimatingOut(cardId);
    
    setTimeout(() => {
      setRemovedCards(prev => new Set([...Array.from(prev), cardId]));
      setAnimatingOut(null);
      onCardRemoved?.(cardId);
      
      // Move to next card if we're at the end
      if (currentIndex >= availableCards.length - 1) {
        setCurrentIndex(Math.max(0, currentIndex - 1));
      }
    }, 300);
  };

  const nextCard = () => {
    if (currentIndex < availableCards.length - maxVisible) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const prevCard = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  // Auto-advance when cards are removed
  useEffect(() => {
    if (availableCards.length === 0) return;
    
    // If current index is beyond available cards, adjust it
    if (currentIndex >= availableCards.length) {
      setCurrentIndex(Math.max(0, availableCards.length - 1));
    }
  }, [availableCards.length, currentIndex]);

  if (availableCards.length === 0) {
    return (
      <div className={`card-deck empty ${className}`}>
        {emptyState || (
          <div className="empty-deck">
            <div className="empty-deck-icon">üé¥</div>
            <p>No more cards</p>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className={`card-deck ${className}`}>
      {/* Navigation dots */}
      {availableCards.length > maxVisible && (
        <div className="deck-navigation">
          <button 
            className="nav-btn prev"
            onClick={prevCard}
            disabled={currentIndex === 0}
          >
            ‚Üê
          </button>
          
          <div className="deck-dots">
            {Array.from({ length: Math.max(1, availableCards.length - maxVisible + 1) }).map((_, i) => (
              <div
                key={i}
                className={`deck-dot ${i === currentIndex ? 'active' : ''}`}
                onClick={() => setCurrentIndex(i)}
              />
            ))}
          </div>
          
          <button 
            className="nav-btn next"
            onClick={nextCard}
            disabled={currentIndex >= availableCards.length - maxVisible}
          >
            ‚Üí
          </button>
        </div>
      )}

      {/* Card Stack */}
      <div className="deck-container">
        {visibleCards.map((card, index) => {
          const isTop = index === 0;
          const zIndex = maxVisible - index;
          const scale = 1 - (index * 0.05);
          const yOffset = index * 8;
          const rotation = (index - 1) * 2;

          return (
            <SwipeableCard
              key={card.id}
              className={`deck-card ${isTop ? 'top-card' : 'stacked-card'} ${animatingOut === card.id ? 'animating-out' : ''}`}
              style={{
                zIndex,
                transform: `translateY(${yOffset}px) scale(${scale}) rotate(${rotation}deg)`,
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
              }}
              onSwipeLeft={isTop ? () => {
                card.onSwipeLeft?.();
                removeCard(card.id, 'left');
              } : undefined}
              onSwipeRight={isTop ? () => {
                card.onSwipeRight?.();
                removeCard(card.id, 'right');
              } : undefined}
              onSwipeUp={isTop ? () => {
                card.onSwipeUp?.();
                removeCard(card.id, 'up');
              } : undefined}
              onSwipeDown={isTop ? () => {
                card.onSwipeDown?.();
                removeCard(card.id, 'down');
              } : undefined}
              leftAction={card.leftAction}
              rightAction={card.rightAction}
              upAction={card.upAction}
              downAction={card.downAction}
              disabled={!isTop || animatingOut === card.id}
            >
              {card.content}
            </SwipeableCard>
          );
        })}
      </div>

      {/* Card Counter */}
      <div className="deck-counter">
        <span className="current-card">{currentIndex + 1}</span>
        <span className="separator"> / </span>
        <span className="total-cards">{availableCards.length}</span>
      </div>
    </div>
  );
}
