import React, { createContext, useContext, useReducer, useEffect, ReactNode } from 'react';
import { io, Socket } from 'socket.io-client';
import { 
  GameState, 
  Game, 
  Player, 
  TaskInstance, 
  Dispute,
  SocketEvents 
} from '../types';

// Initial State
const initialState: GameState = {
  currentGame: null,
  currentPlayer: null,
  isConnected: false,
  isLoading: false,
  error: null,
  activeDisputes: [],
  leaderboard: []
};

// Actions
type GameAction = 
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'SET_ERROR'; payload: string | null }
  | { type: 'SET_CONNECTION'; payload: boolean }
  | { type: 'SET_GAME'; payload: Game | null }
  | { type: 'SET_PLAYER'; payload: Player | null }
  | { type: 'UPDATE_PLAYER'; payload: Player }
  | { type: 'ADD_PLAYER'; payload: Player }
  | { type: 'REMOVE_PLAYER'; payload: string }
  | { type: 'UPDATE_TASK'; payload: TaskInstance }
  | { type: 'ADD_DISPUTE'; payload: Dispute }
  | { type: 'UPDATE_DISPUTE'; payload: Dispute }
  | { type: 'REMOVE_DISPUTE'; payload: string }
  | { type: 'UPDATE_LEADERBOARD'; payload: Player[] };

// Reducer
function gameReducer(state: GameState, action: GameAction): GameState {
  switch (action.type) {
    case 'SET_LOADING':
      return { ...state, isLoading: action.payload };
    
    case 'SET_ERROR':
      return { ...state, error: action.payload };
    
    case 'SET_CONNECTION':
      return { ...state, isConnected: action.payload };
    
    case 'SET_GAME':
      return { ...state, currentGame: action.payload };
    
    case 'SET_PLAYER':
      return { ...state, currentPlayer: action.payload };
    
    case 'UPDATE_PLAYER':
      if (!state.currentGame) return state;
      return {
        ...state,
        currentGame: {
          ...state.currentGame,
          players: state.currentGame.players.map(p => 
            p.id === action.payload.id ? action.payload : p
          )
        },
        currentPlayer: state.currentPlayer?.id === action.payload.id 
          ? action.payload 
          : state.currentPlayer
      };
    
    case 'ADD_PLAYER':
      if (!state.currentGame) return state;
      return {
        ...state,
        currentGame: {
          ...state.currentGame,
          players: [...state.currentGame.players, action.payload]
        }
      };
    
    case 'REMOVE_PLAYER':
      if (!state.currentGame) return state;
      return {
        ...state,
        currentGame: {
          ...state.currentGame,
          players: state.currentGame.players.filter(p => p.id !== action.payload)
        }
      };
    
    case 'UPDATE_TASK':
      if (!state.currentPlayer) return state;
      return {
        ...state,
        currentPlayer: {
          ...state.currentPlayer,
          tasks: state.currentPlayer.tasks.map(t => 
            t.id === action.payload.id ? action.payload : t
          )
        }
      };
    
    case 'ADD_DISPUTE':
      return {
        ...state,
        activeDisputes: [...state.activeDisputes, action.payload]
      };
    
    case 'UPDATE_DISPUTE':
      return {
        ...state,
        activeDisputes: state.activeDisputes.map(d => 
          d.id === action.payload.id ? action.payload : d
        )
      };
    
    case 'REMOVE_DISPUTE':
      return {
        ...state,
        activeDisputes: state.activeDisputes.filter(d => d.id !== action.payload)
      };
    
    case 'UPDATE_LEADERBOARD':
      return {
        ...state,
        leaderboard: action.payload
      };
    
    default:
      return state;
  }
}

// Context
interface GameContextType {
  state: GameState;
  socket: Socket | null;
  // Game actions
  createGame: (mode: 'casual' | 'competitive', playerName: string) => Promise<void>;
  joinGame: (gameId: string, playerName: string) => Promise<void>;
  startGame: () => Promise<void>;
  endGame: () => Promise<void>;
  
  // Player actions
  lockInPlayer: () => Promise<void>;
  updatePlayerName: (name: string) => Promise<void>;
  
  // Task actions
  swapTask: (taskId: string) => Promise<void>;
  claimGotcha: (taskId: string, targetId: string) => Promise<void>;
  
  // Dispute actions
  disputeGotcha: (taskId: string, reason?: string) => Promise<void>;
  voteOnDispute: (disputeId: string, vote: boolean) => Promise<void>;
  acceptGotcha: (taskId: string) => Promise<void>;
}

const GameContext = createContext<GameContextType | undefined>(undefined);

// Socket instance
let socket: Socket | null = null;

// Provider Component
interface GameProviderProps {
  children: ReactNode;
}

export function GameProvider({ children }: GameProviderProps) {
  const [state, dispatch] = useReducer(gameReducer, initialState);

  // Initialize socket connection
  useEffect(() => {
    // TODO: Replace mock with real Socket.IO connection once backend is ready
    // For now we skip setting up a socket so TypeScript doesn’t complain about
    // methods like .on() on an uninitialised object.
    dispatch({ type: 'SET_CONNECTION', payload: false });
    return () => {
      if (socket) {
        socket.disconnect?.();
        socket = null;
      }
    };
  }, []);

  // Game Actions
  const createGame = async (mode: 'casual' | 'competitive', playerName: string) => {
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });
    
    try {
      const gameId = Math.random().toString(36).substr(2, 9).toUpperCase();
      const playerId = Math.random().toString(36).substr(2, 9);
      
      // Import task functions
      const { getRandomTasks } = await import('../data/mockTasks');
      const playerTasks = getRandomTasks('core-a', 7);
      
      // Create mock player
      const mockPlayer: Player = {
        id: playerId,
        name: playerName,
        gameId: gameId,
        swapsLeft: mode === 'casual' ? 2 : 1,
        score: 0,
        lockedIn: false,
        isHost: true,
        token: 'mock-token',
        tasks: playerTasks.map((task, index) => ({
          id: `task-instance-${index}`,
          gameId: gameId,
          playerId: playerId,
          text: task.text,
          difficulty: task.difficulty,
          status: 'pending' as const,
          packId: 'core-a',
          originalIndex: index
        })),
        stats: {
          gothcas: 0,
          failed: 0,
          disputesLost: 0,
          uniqueTargets: [],
          firstTimeTargets: 0
        }
      };

      const mockGame: Game = {
        id: gameId,
        status: 'draft',
        mode,
        packId: 'core-a',
        createdBy: playerName,
        createdAt: new Date(),
        hostId: playerId,
        players: [mockPlayer],
        settings: {
          swapsAllowed: mode === 'casual' ? 2 : 1,
          disputeTimeoutSeconds: 120,
          hostDefaultOnTie: true,
          enableNegativeScoring: mode === 'competitive',
          maxPlayers: 8,
          targetScore: 4
        },
        currentPhase: 'draft'
      };
      
      dispatch({ type: 'SET_GAME', payload: mockGame });
      dispatch({ type: 'SET_PLAYER', payload: mockPlayer });
      
      // Store game info for rejoin
      localStorage.setItem('currentGameId', mockGame.id);
      localStorage.setItem('currentPlayerId', playerId);
      localStorage.setItem('gameData', JSON.stringify(mockGame));
      localStorage.setItem('playerToken', 'mock-token');
      
    } catch (error) {
      dispatch({ type: 'SET_ERROR', payload: 'Failed to create game' });
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  };

  const joinGame = async (gameId: string, playerName: string) => {
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });
    
    try {
      // Try to load existing game from localStorage
      const savedGameData = localStorage.getItem('gameData');
      let game: Game | null = null;
      
      if (savedGameData) {
        const parsedGame = JSON.parse(savedGameData);
        if (parsedGame.id === gameId) {
          game = parsedGame;
        }
      }
      
      if (!game) {
        throw new Error('Game not found');
      }
      
      // Check if player already exists
      let existingPlayer = game.players.find(p => p.name === playerName);
      
      if (!existingPlayer) {
        // Create new player
        const playerId = Math.random().toString(36).substr(2, 9);
        const { getRandomTasks } = await import('../data/mockTasks');
        const playerTasks = getRandomTasks('core-a', 7);
        
        const newPlayer: Player = {
          id: playerId,
          name: playerName,
          gameId: gameId,
          swapsLeft: game.settings.swapsAllowed,
          score: 0,
          lockedIn: false,
          isHost: false,
          token: 'mock-token',
          tasks: playerTasks.map((task, index) => ({
            id: `task-instance-${playerId}-${index}`,
            gameId: gameId,
            playerId: playerId,
            text: task.text,
            difficulty: task.difficulty,
            status: 'pending' as const,
            packId: 'core-a',
            originalIndex: index
          })),
          stats: {
            gothcas: 0,
            failed: 0,
            disputesLost: 0,
            uniqueTargets: [],
            firstTimeTargets: 0
          }
        };
        
        game.players.push(newPlayer);
        existingPlayer = newPlayer;
      }
      
      dispatch({ type: 'SET_GAME', payload: game });
      dispatch({ type: 'SET_PLAYER', payload: existingPlayer });
      
      // Update localStorage
      localStorage.setItem('currentGameId', gameId);
      localStorage.setItem('currentPlayerId', existingPlayer.id);
      localStorage.setItem('gameData', JSON.stringify(game));
      localStorage.setItem('playerToken', 'mock-token');
      
    } catch (error) {
      dispatch({ type: 'SET_ERROR', payload: 'Failed to join game. Make sure the game ID is correct.' });
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  };

  const startGame = async () => {
    if (socket && state.currentGame) {
      socket.emit('game:start', { gameId: state.currentGame.id });
    }
  };

  const endGame = async () => {
    if (socket && state.currentGame) {
      socket.emit('game:end', { gameId: state.currentGame.id });
    }
  };

  const lockInPlayer = async () => {
    if (socket && state.currentPlayer) {
      socket.emit('player:lock', { playerId: state.currentPlayer.id });
    }
  };

  const updatePlayerName = async (name: string) => {
    if (socket && state.currentPlayer) {
      socket.emit('player:update', { 
        playerId: state.currentPlayer.id, 
        updates: { name } 
      });
    }
  };

  const swapTask = async (taskId: string) => {
    if (socket && state.currentPlayer) {
      socket.emit('task:swap', { 
        playerId: state.currentPlayer.id, 
        taskId 
      });
    }
  };

  const claimGotcha = async (taskId: string, targetId: string) => {
    if (socket) {
      socket.emit('task:gotcha', { taskId, targetId });
    }
  };

  const disputeGotcha = async (taskId: string, reason?: string) => {
    if (socket) {
      socket.emit('dispute:create', { taskId, reason });
    }
  };

  const voteOnDispute = async (disputeId: string, vote: boolean) => {
    if (socket && state.currentPlayer) {
      socket.emit('dispute:vote', { 
        disputeId, 
        vote, 
        voterId: state.currentPlayer.id 
      });
    }
  };

  const acceptGotcha = async (taskId: string) => {
    if (socket) {
      socket.emit('task:accept', { taskId });
    }
  };

  const contextValue: GameContextType = {
    state,
    socket,
    createGame,
    joinGame,
    startGame,
    endGame,
    lockInPlayer,
    updatePlayerName,
    swapTask,
    claimGotcha,
    disputeGotcha,
    voteOnDispute,
    acceptGotcha
  };

  return (
    <GameContext.Provider value={contextValue}>
      {children}
    </GameContext.Provider>
  );
}

// Hook
export function useGame() {
  const context = useContext(GameContext);
  if (context === undefined) {
    throw new Error('useGame must be used within a GameProvider');
  }
  return context;
}
