import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';

export default function Home() {
  const navigate = useNavigate();
  const { state, createGame, joinGame } = useGame();
  const [playerName, setPlayerName] = useState('');
  const [gameId, setGameId] = useState('');
  const [showJoinForm, setShowJoinForm] = useState(false);
  const [selectedMode, setSelectedMode] = useState<'casual' | 'competitive'>('casual');

  // Check for existing game session
  useEffect(() => {
    const existingGameId = localStorage.getItem('currentGameId');
    const existingPlayerId = localStorage.getItem('currentPlayerId');
    
    if (existingGameId && existingPlayerId) {
      setGameId(existingGameId);
    }
  }, []);

  // Navigate to game when one is created/joined
  useEffect(() => {
    if (state.currentGame) {
      if (state.currentGame.status === 'draft') {
        navigate(`/lobby/${state.currentGame.id}`);
      } else if (state.currentGame.status === 'live') {
        navigate(`/game/${state.currentGame.id}`);
      } else if (state.currentGame.status === 'ended') {
        navigate(`/recap/${state.currentGame.id}`);
      }
    }
  }, [state.currentGame, navigate]);

  const handleCreateGame = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!playerName.trim()) return;
    
    await createGame(selectedMode, playerName.trim());
  };

  const handleJoinGame = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!playerName.trim() || !gameId.trim()) return;
    
    await joinGame(gameId.trim().toUpperCase(), playerName.trim());
  };

  const handleQuickJoin = () => {
    const existingGameId = localStorage.getItem('currentGameId');
    if (existingGameId && state.currentGame) {
      if (state.currentGame.status === 'draft') {
        navigate(`/lobby/${existingGameId}`);
      } else if (state.currentGame.status === 'live') {
        navigate(`/game/${existingGameId}`);
      } else if (state.currentGame.status === 'ended') {
        navigate(`/recap/${existingGameId}`);
      }
    }
  };

  const addDemoPlayer = async () => {
    if (!state.currentGame || !gameId) return;
    
    const demoNames = ['Alice', 'Bob', 'Charlie', 'Diana', 'Eve', 'Frank'];
    const usedNames = state.currentGame.players.map(p => p.name);
    const availableNames = demoNames.filter(name => !usedNames.includes(name));
    
    if (availableNames.length === 0) {
      alert('No more demo players available');
      return;
    }
    
    const demoName = availableNames[0];
    await joinGame(gameId, demoName);
  };

  const clearDemoData = () => {
    localStorage.removeItem('gameData');
    localStorage.removeItem('currentGameId');
    localStorage.removeItem('currentPlayerId');
    localStorage.removeItem('playerToken');
    window.location.reload();
  };

  const switchPlayer = (playerId: string) => {
    if (!state.currentGame) return;
    
    const player = state.currentGame.players.find(p => p.id === playerId);
    if (player) {
      localStorage.setItem('currentPlayerId', playerId);
      window.location.reload();
    }
  };

  const quickTestGame = async () => {
    // Create a game with multiple players and set it to live for testing
    await createGame('casual', 'TestHost');
    
    // Wait for game to be created, then add demo players
    setTimeout(async () => {
      const demoNames = ['Alice', 'Bob', 'Charlie'];
      for (const name of demoNames) {
        await joinGame(state.currentGame?.id || '', name);
      }
    }, 100);
  };

  return (
    <div className="flex flex-column flex-center" style={{ minHeight: '100vh' }}>
      <div style={{ maxWidth: '400px', width: '100%', padding: '1rem' }}>
        <div className="text-center" style={{ marginBottom: '2rem' }}>
          <h1>Who Got Who</h1>
          <p className="text-small">
            The ultimate party game of stealth tasks and gotchas!
          </p>
        </div>

        {state.error && (
          <div className="card" style={{ backgroundColor: '#dc3545', marginBottom: '1rem' }}>
            <p>{state.error}</p>
          </div>
        )}

        {/* Quick Rejoin */}
        {gameId && state.currentGame && (
          <div className="card" style={{ marginBottom: '1rem' }}>
            <h3>Continue Game</h3>
            <p className="text-small">
              Game ID: {gameId} • Status: {state.currentGame.status}
              <br />
              Players: {state.currentGame.players.length}
            </p>
            <button 
              className="btn btn-success" 
              onClick={handleQuickJoin}
              disabled={state.isLoading}
            >
              {state.currentGame.status === 'draft' ? 'Return to Lobby' :
               state.currentGame.status === 'live' ? 'Rejoin Game' :
               'View Results'}
            </button>
            
            {state.currentGame.status === 'draft' && state.currentGame.players.length < 8 && (
              <button 
                className="btn btn-secondary"
                onClick={addDemoPlayer}
                disabled={state.isLoading}
                style={{ marginTop: '0.5rem' }}
              >
                + Add Demo Player ({state.currentGame.players.length}/8)
              </button>
            )}
          </div>
        )}

        {/* Demo Notice */}
        <div className="card" style={{ backgroundColor: '#2d4a87', marginBottom: '1rem' }}>
          <h3>📱 Local Demo Mode</h3>
          <p className="text-small">
            This is a <strong>local demo</strong> using browser storage. 
            You can test the full game flow including:
            <br />• Task assignment and swapping
            <br />• Gotcha claiming and scoring  
            <br />• Multiple players on same device
            <br />• Game progression and recap
          </p>
          {gameId && state.currentGame && (
            <div style={{ marginTop: '0.5rem' }}>
              <button 
                className="btn btn-danger btn-small"
                onClick={clearDemoData}
                style={{ margin: '0.25rem' }}
              >
                🗑️ Clear Demo Data
              </button>
            </div>
          )}
        </div>

        {/* Testing Instructions */}
        <div className="card" style={{ backgroundColor: '#1a4d2e', marginBottom: '1rem' }}>
          <h3>🧪 How to Test Multiple Players</h3>
          <div className="text-small">
            <p><strong>Option 1: Incognito Windows</strong></p>
            <p>• Open incognito/private windows in your browser</p>
            <p>• Each window has separate storage = different player</p>
            <p>• Create game in one, join from others using Game ID</p>
            
            <p style={{ marginTop: '1rem' }}><strong>Option 2: Different Browsers</strong></p>
            <p>• Use Chrome + Firefox + Edge simultaneously</p>
            <p>• Each browser = different player with own storage</p>
            
            <p style={{ marginTop: '1rem' }}><strong>Option 3: Player Switching (Current Window)</strong></p>
            {state.currentGame && state.currentGame.players.length > 1 && (
              <div style={{ marginTop: '0.5rem' }}>
                <p>Switch between players in this window:</p>
                {state.currentGame.players.map(player => (
                  <button
                    key={player.id}
                    className={`btn btn-small ${player.id === state.currentPlayer?.id ? 'btn-success' : 'btn-secondary'}`}
                    onClick={() => switchPlayer(player.id)}
                    style={{ margin: '0.25rem' }}
                  >
                    {player.name} {player.isHost ? '(Host)' : ''}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Quick Test Setup */}
        {!gameId && (
          <div className="card" style={{ backgroundColor: '#4a2d4a', marginBottom: '1rem' }}>
            <h3>⚡ Quick Test Setup</h3>
            <p className="text-small">
              Create a game pre-filled with demo players for quick testing
            </p>
            <button 
              className="btn"
              onClick={quickTestGame}
              disabled={state.isLoading}
            >
              🚀 Create Test Game with Demo Players
            </button>
          </div>
        )}

        {!showJoinForm ? (
          <div>
            {/* Create Game Form */}
            <div className="card">
              <h2>Create New Game</h2>
              <form onSubmit={handleCreateGame}>
                <input
                  type="text"
                  className="input"
                  placeholder="Your name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  maxLength={20}
                  required
                />
                
                <div style={{ margin: '1rem 0' }}>
                  <h3>Game Mode</h3>
                  <div className="flex gap-1" style={{ flexDirection: 'column' }}>
                    <label className="flex" style={{ alignItems: 'center', gap: '0.5rem' }}>
                      <input
                        type="radio"
                        value="casual"
                        checked={selectedMode === 'casual'}
                        onChange={(e) => setSelectedMode(e.target.value as 'casual')}
                      />
                      <div>
                        <strong>Casual</strong>
                        <div className="text-small">2 swaps, no negative scoring</div>
                      </div>
                    </label>
                    <label className="flex" style={{ alignItems: 'center', gap: '0.5rem' }}>
                      <input
                        type="radio"
                        value="competitive"
                        checked={selectedMode === 'competitive'}
                        onChange={(e) => setSelectedMode(e.target.value as 'competitive')}
                      />
                      <div>
                        <strong>Competitive</strong>
                        <div className="text-small">1 swap, penalties for fails & disputes</div>
                      </div>
                    </label>
                  </div>
                </div>

                <button 
                  type="submit" 
                  className="btn" 
                  disabled={state.isLoading || !playerName.trim()}
                >
                  {state.isLoading ? 'Creating...' : 'Create Game'}
                </button>
              </form>
            </div>

            <div className="text-center" style={{ margin: '1rem 0' }}>
              <button 
                className="btn btn-secondary" 
                onClick={() => setShowJoinForm(true)}
              >
                Join Existing Game
              </button>
            </div>
          </div>
        ) : (
          <div>
            {/* Join Game Form */}
            <div className="card">
              <h2>Join Game</h2>
              <form onSubmit={handleJoinGame}>
                <input
                  type="text"
                  className="input"
                  placeholder="Your name"
                  value={playerName}
                  onChange={(e) => setPlayerName(e.target.value)}
                  maxLength={20}
                  required
                />
                <input
                  type="text"
                  className="input"
                  placeholder="Game ID"
                  value={gameId}
                  onChange={(e) => setGameId(e.target.value.toUpperCase())}
                  maxLength={10}
                  required
                />
                <button 
                  type="submit" 
                  className="btn" 
                  disabled={state.isLoading || !playerName.trim() || !gameId.trim()}
                >
                  {state.isLoading ? 'Joining...' : 'Join Game'}
                </button>
              </form>
            </div>

            <div className="text-center" style={{ margin: '1rem 0' }}>
              <button 
                className="btn btn-secondary" 
                onClick={() => setShowJoinForm(false)}
              >
                Create New Game Instead
              </button>
            </div>
          </div>
        )}

        {/* Game Rules Summary */}
        <div className="card" style={{ marginTop: '2rem' }}>
          <h3>Quick Rules</h3>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            <li>🎯 Complete stealth tasks on other players</li>
            <li>🗣️ Say "Gotcha!" when done</li>
            <li>🏆 First to 4 wins (or highest when cards run out)</li>
            <li>⚖️ Target can dispute within 2 minutes</li>
            <li>🔄 Swap unwanted tasks before game starts</li>
          </ul>
        </div>

        {/* Connection Status */}
        <div className="text-center text-small" style={{ marginTop: '1rem', opacity: 0.6 }}>
          Status: {state.isConnected ? '🟢 Connected' : '🔴 Offline Mode'}
        </div>
      </div>
    </div>
  );
}
