  { id: 'task-015', text: 'Have someone make a sound effect', difficulty: 3, category: 'action', tags: ['sound', 'expression'] },
  
  // Misdirection tasks (difficulty 3-4)
  { id: 'task-016', text: 'Get someone to spell a word backwards', difficulty: 4, category: 'misdirection', tags: ['spelling', 'mental challenge'] },
  { id: 'task-017', text: 'Make someone guess what you\'re thinking', difficulty: 3, category: 'misdirection', tags: ['guessing', 'mind reading'] },
  { id: 'task-018', text: 'Get someone to describe their morning routine', difficulty: 3, category: 'misdirection', tags: ['personal', 'routine'] },
