  { id: 'task-021', text: 'Get someone to imitate an animal sound', difficulty: 3, category: 'creative', tags: ['imitation', 'animal'] },
  { id: 'task-022', text: 'Have someone create a handshake with you', difficulty: 4, category: 'creative', tags: ['collaboration', 'gesture'] },
